import React from 'react';
import './App.css';
import Particles from 'react-particles-js';
import Microphone from './Microphone';
import Logo from './Logo';
import anime from 'animejs';

const App=()=>{

    return (
      <div className="App">
      <Particles className='parti'
     params={{
         particles: {
           number:{
             value:40,
             density:{
               enable:true,
               value_area:800
             }
           },
          move:
          {
            enable:true,
            speed:7
          },

         }
       }}/>
       <Logo params = {anime({
     targets: 'div',
     translateX: [
       { value: 100, duration: 1200 },
       { value: 0, duration: 800 }
     ],
     rotate: '1turn',
     backgroundColor: 'linear-gradient(to right, #051937, #004d7a, #008793, #00bf72, #a8eb12);  -webkit-font-smoothing: antialiased',
     duration: 2000,
     loop: true
   }) }/>
      <Microphone />
      </div>
    );

}

export default App;
