import React from 'react';
import './App.css';
import Particles from 'react-particles-js';
import Logo from './Logo';
import Dictaphone from './Dictaphone';


class App extends React.Component{

  onLogoClick=()=>{
    {window.responsiveVoice.speak('Welcome to smart switch')};
    {window.responsiveVoice.speak('Lights have been on for a long time.')};
    {window.responsiveVoice.speak('Do you still want them to be on?')};
  }

render(){
    return (

      <div className="App">
      <Particles className='parti'
     params={{
         particles: {
           number:{
             value:40,
             density:{
               enable:true,
               value_area:800
             }
           },
          move:
          {
            enable:true,
            speed:7
          },

         }
       }}/>
      <Logo onLogoClick={this.onLogoClick}/>
      <Dictaphone />
      </div>
    );
 }
}

export default App;
