import React, {Component } from 'react';
import SpeechRecognition from 'react-speech-recognition';
import './Dictaphone.css';

const options ={
  autoStart:false
}

class Dictaphone extends Component {
  render() {
    const { transcript, resetTranscript, browserSupportsSpeechRecognition } = this.props

    if (!browserSupportsSpeechRecognition) {
      return null
    }

    return (
      <div className='measure center pa4 black-80'>
        <button className='grow bg-light-green f4' onClick={resetTranscript}>Reset</button>
        <input id="input" className="tc pa2 input-reset ba bg-green hover-white w-100" type="text" value={transcript}/>
      </div>
    )
  }
}

export default SpeechRecognition(options)(Dictaphone);
