import React from 'react';
import './App.css';
import Particles from 'react-particles-js';
import Microphone from './Microphone';
import Logo from './Logo';

class App extends React.Component{

constructor(){
  super();
  this.state ={
    input:''
  }
}

inputState=(response)=>{
  this.setState({input: data.audio.content});
}

onButtonClick=()=>{
  fetch('https://speech.googleapis.com/v1p1beta1/speech:recognize')
  .then(response=>response.json())
  .then(data=>inputState(data))
}

render(){
    return (
      <div className="App">
      <Particles className='parti'
     params={{
         particles: {
           number:{
             value:40,
             density:{
               enable:true,
               value_area:800
             }
           },
          move:
          {
            enable:true,
            speed:7
          },

         }
       }}/>
      <Logo />
      <Microphone onButtonClick={this.onButtonClick}/>
      </div>
    );
 }
}

export default App;
