import React from 'react';
import './App.css';
import Particles from 'react-particles-js';
import Logo from './Logo';
import Dictaphone from './Dictaphone';
import { VoicePlayer, VoiceRecognition } from 'react-voice-components';

class App extends React.Component{



render(){
    return (
      <div className="App">
      <VoicePlayer
      play
      text="Welcome to the smart switch. The lights have been turned on for a long timeout do you still want them on? "/>
      <Particles className='parti'
     params={{
         particles: {
           number:{
             value:40,
             density:{
               enable:true,
               value_area:800
             }
           },
          move:
          {
            enable:true,
            speed:7
          },

         }
       }}/>
      <Logo />
      <Dictaphone />
      </div>
    );
 }
}

export default App;
