import React, {Component } from 'react';
import SpeechRecognition from 'react-speech-recognition';
import './Dictaphone.css';
import Mic from './Mic.png'

const options ={
  autoStart:false
}


class Dictaphone extends Component {

  constructor(){
    super();
    this.state={
      input:''
    }
  }


  render() {
    const { stopListening,startListening,transcript, resetTranscript, browserSupportsSpeechRecognition } = this.props

    if (!browserSupportsSpeechRecognition) {
      return null
    }

    return (
      <div className='measure center pa4 black-80'>
        <button className='ma2 pa2 grow bg-light-green f4' onClick={resetTranscript}>Reset</button>
        <input id="input" className="tc pa2 input-reset ba bg-green hover-white w-100" type="text" value={transcript}/>
        <img className='ma2' alt='' src={Mic} onClick={startListening} />
        <div>
        <button className='grow pa2 bg-light-green f4' onClick={stopListening} >Stop!</button>
        </div>
      </div>
    )
  }
}

export default SpeechRecognition(options)(Dictaphone);
